const gameData = msg.payload;

// Transform game data to schedule format
const scheduleData = {
    date: gameData.date || new Date().toISOString().split('T')[0],
    person: gameData.person || 'Player',
    score: gameData.score || 0,
    timestamp: gameData.timestamp,

    // Map game metrics to schedule metrics
    energyLevel: Math.min(5, Math.max(1, Math.round((gameData.gameData.energy / 10) * 5) || 3)),
    taskProductivity: Math.min(5, Math.max(1, Math.round((gameData.gameData.tasks / 8) * 5) || 3)),
    focusQuality: Math.min(5, Math.max(1, Math.round((gameData.gameData.focus / 6) * 5) || 3)),
    socialEngagement: Math.min(5, Math.max(1, Math.round((gameData.gameData.social / 4) * 5) || 3)),
    distractionLevel: Math.max(1, Math.min(5, 5 - Math.round((gameData.gameData.distractions / 3) * 4))),

    // Calculate derived metrics
    overallProductivity: Math.round((gameData.score / 100) * 5) || 1,
    gameMetrics: gameData.gameData,
    rawGameData: gameData
};

// Add engagement score based on game performance
scheduleData.engagementScore = Math.round(
    (scheduleData.energyLevel + scheduleData.taskProductivity + scheduleData.focusQuality) / 3
);

msg.payload = scheduleData;
return msg;

const data = msg.payload;

// Calculate weighted productivity score
const weightedProductivity = (
    (data.energyLevel * 0.3) +
    (data.taskProductivity * 0.4) +
    (data.focusQuality * 0.3)
);

// Calculate attention efficiency
const attentionEfficiency = data.focusQuality / Math.max(1, data.distractionLevel);

// Calculate social balance score
const socialBalance = Math.min(5, data.socialEngagement + (data.focusQuality * 0.2));

// Generate insights based on game performance
let insights = [];
if (data.energyLevel >= 4) {
    insights.push('High energy day - great for challenging tasks!');
} else if (data.energyLevel <= 2) {
    insights.push('Low energy detected - consider rest and recovery.');
}

if (data.taskProductivity >= 4) {
    insights.push('Excellent task completion rate!');
} else if (data.taskProductivity <= 2) {
    insights.push('Task productivity could be improved.');
}

if (data.focusQuality >= 4) {
    insights.push('Outstanding focus quality!');
} else if (data.focusQuality <= 2) {
    insights.push('Focus quality needs attention.');
}

if (data.gameMetrics.distractions > 5) {
    insights.push('High distraction count - consider environment optimization.');
}

// Add calculated metrics to payload
msg.payload.calculations = {
    weightedProductivity: Math.round(weightedProductivity * 100) / 100,
    attentionEfficiency: Math.round(attentionEfficiency * 100) / 100,
    socialBalance: Math.round(socialBalance * 100) / 100,
    insights: insights,
    recommendedActions: generateRecommendations(data)
};

function generateRecommendations(data) {
    const recommendations = [];

    if (data.energyLevel <= 2) {
        recommendations.push('Take a break and recharge');
        recommendations.push('Consider adjusting sleep schedule');
    }

    if (data.taskProductivity <= 2) {
        recommendations.push('Break tasks into smaller chunks');
        recommendations.push('Use time-blocking techniques');
    }

    if (data.focusQuality <= 2) {
        recommendations.push('Minimize distractions in workspace');
        recommendations.push('Try the Pomodoro technique');
    }

    if (data.socialEngagement <= 2) {
        recommendations.push('Schedule social interactions');
        recommendations.push('Take collaborative breaks');
    }

    return recommendations;
}

return msg;

