const data = msg.payload;

// Create detailed insights report
const insightsReport = {
    date: data.date,
    person: data.person,
    summary: {
        overallScore: data.score,
        productivityLevel: getProductivityLevel(data.calculations.weightedProductivity),
        focusState: getFocusState(data.focusQuality),
        energyState: getEnergyState(data.energyLevel)
    },
    gameMetrics: data.gameMetrics,
    insights: data.calculations.insights,
    recommendations: data.calculations.recommendedActions,
    trends: {
        energyTrend: data.energyLevel >= 4 ? 'high' : data.energyLevel <= 2 ? 'low' : 'moderate',
        productivityTrend: data.taskProductivity >= 4 ? 'high' : data.taskProductivity <= 2 ? 'low' : 'moderate',
        focusTrend: data.focusQuality >= 4 ? 'excellent' : data.focusQuality <= 2 ? 'poor' : 'good'
    },
    timestamp: data.timestamp
};

function getProductivityLevel(score) {
    if (score >= 4) return 'High Productivity';
    if (score >= 3) return 'Moderate Productivity';
    if (score >= 2) return 'Low Productivity';
    return 'Very Low Productivity';
}

function getFocusState(score) {
    if (score >= 4) return 'Excellent Focus';
    if (score >= 3) return 'Good Focus';
    if (score >= 2) return 'Fair Focus';
    return 'Poor Focus';
}

function getEnergyState(score) {
    if (score >= 4) return 'High Energy';
    if (score >= 3) return 'Moderate Energy';
    if (score >= 2) return 'Low Energy';
    return 'Very Low Energy';
}

msg.payload = insightsReport;
return msg;

const data = msg.payload;

// Create response for the game frontend
const response = {
